import React, { useEffect, useState } from "react";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as Yup from "yup";
import { useNavigate } from "react-router-dom";
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
// import axios from "axios";

const baseURL = "http://localhost:3030/";

export default function RegistrationForm() {
  const [user, setUser] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    fetch(`${baseURL}`)
      .then((res) => {
        res.json();
      })
      .then((data) => {
        setUser(data);
      })
      .catch((err) => {
        console.log(err.message);
      });
  }, []);

  //Validation.....
  const schema = Yup.object().shape({
    Name: Yup.string().required("Name is required field!"),
    Email: Yup.string()
      .required("Email is required field!")
      .email("Format error"),
    password: Yup.string()
      .required("Password Required!")
      .min(6, "Password must be at least 6 characters"),
    confirmpwd: Yup.string()
      .required("Confirm password is required!")
      .oneOf([Yup.ref("password")], "Confirm password must be same!"),
  });

  const formresolver = { resolver: yupResolver(schema) };
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm(formresolver);

  const onSubmit = (data) => {
    fetch(`${baseURL}signup`, {
      method: "POST",
      body: JSON.stringify({
        name: data.Name,
        email: data.Email,
        password: data.password,
      }),
      headers: {
        "Content-type": "application/json; charset=UTF-8",
      },
    }).then(res => {
      // console.log(res);
      if (res.status !== 409) {
        toast.success("Registration Successful! Enjoy the journey.")
        navigate("/login");
      } else {
        toast.warning("Email already registred. Please LogIn!")
        navigate("/login");
      }
    })
  };
  return (
    <div className="border border-warning" style={{ marginTop: "3.5rem" }}>
      <form onSubmit={handleSubmit(onSubmit)} noValidate>
        <section className="vh-100" style={{ backgroundImage: "linear-gradient(to right, #6a11cb 0%, #2575fc 100%)" }}>
          <div className="container h-100">
            <div className="row d-flex justify-content-center align-items-center h-100">
              <div className="col-xl-9">
                <h1 className="text-white mb-4">Registration Form</h1>

                <div className="card" style={{ borderRadius: "15px" }}>
                  <div className="card-body">
                    <div className="row align-items-center pt-4 pb-3">
                      <div className="col-md-3 ps-5">
                        <h6 className="mb-0">Full name</h6>
                      </div>
                      <div className="col-md-9 pe-5">
                        <input
                          {...register("Name")}
                          type="text"
                          className={`form-control form-control-lg ${errors.Name ? "is-invalid" : ""
                            }`}
                        />
                      </div>
                      {/* <div className="invalid-feedback">
                        {errors.Name?.message}
                      </div> */}
                    </div>

                    <hr className="mx-n3" />

                    <div className="row align-items-center py-3">
                      <div className="col-md-3 ps-5">
                        <h6 className="mb-0">Email address</h6>
                      </div>
                      <div className="col-md-9 pe-5">
                        <input
                          {...register("Email")}
                          type="email"
                          // className="form-control form-control-lg"
                          placeholder="example@example.com"
                          className={`form-control form-control-lg ${errors.Email ? "is-invalid" : ""
                            }`}
                        />
                      </div>
                      <div className="invalid-feedback">
                        {errors.Email?.message}
                      </div>
                    </div>

                    <hr className="mx-n3" />

                    <div className="row align-items-center py-3">
                      <div className="col-md-3 ps-5">
                        <h6 className="mb-0">Password</h6>
                      </div>
                      <div className="col-md-9 pe-5">
                        <input
                          name="password"
                          {...register("password")}
                          className={`form-control form-control-lg ${errors.password ? "is-invalid" : ""
                            }`}
                          placeholder="Password"
                          type="password"
                        />
                        <div className="invalid-feedback">
                          {errors.password?.message}
                        </div>
                      </div>
                    </div>
                    <div className="row align-items-center py-3">
                      <div className="col-md-3 ps-5">
                        <h6 className="mb-0">Confirm Password</h6>
                      </div>
                      <div className="col-md-9 pe-5">
                        <input
                          {...register("confirmpwd")}
                          className={`form-control form-control-lg ${errors.confirmpwd ? "is-invalid" : ""
                            }`}
                          placeholder="Confirm Password"
                          type="password"
                          name="confirmpwd"
                        />
                        <div className="invalid-feedback">
                          {errors.confirmpwd?.message}
                        </div>
                      </div>
                    </div>
                    <hr className="mx-n3" />

                    <div className="px-5 py-4">
                      <button
                        type="submit"
                        className="btn btn-outline-warning btn-lg m-3"
                      >
                        Register
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </section>
      </form>
    </div>
  );
}
